import { useCallback, useState } from 'react';
import ReactFlow, {
  addEdge,
  applyEdgeChanges,
  applyNodeChanges,
  MiniMap,
  Controls,
  Background,
  useReactFlow,
  ReactFlowProvider
} from 'reactflow';
import 'reactflow/dist/style.css';

import DatasetNode from './nodes/dataset/DatasetNode.js';
import TargetNode from './nodes/preprocessing/target/TargetNode.js';

import './nodes/dataset/dataset-node.css';
import './nodes/preprocessing/target/target-node.css';

const initialNodes = [
  {
    id: 'node-1',
    type: 'datasetnode',
    position: { x: 0, y: 0 }
  },
  {
    id: 'node-2',
    type: 'targetnode',
    targetPosition: 'top',
    position: { x: 200, y: 0 },
    data: { label: 'node 2' },
    sourcePosition: 'right',
    targetPosition: 'left'
  }
];

const initialEdges = [
  { id: 'edge-1', source: 'node-1', target: 'node-2', sourceHandle: 'a' },
];

// we define the nodeTypes outside of the component to prevent re-renderings
// you could also use useMemo inside the component
const nodeTypes = { 
  datasetnode: DatasetNode,
  targetnode: TargetNode
};

let nodeId = 0

function Flow() {
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState(initialEdges);

  const onNodesChange = useCallback(
    (changes) => setNodes((nds) => applyNodeChanges(changes, nds)),
    [setNodes]
  );
  const onEdgesChange = useCallback(
    (changes) => setEdges((eds) => applyEdgeChanges(changes, eds)),
    [setEdges]
  );
  const onConnect = useCallback(
    (connection) => setEdges((eds) => addEdge(connection, eds)),
    [setEdges]
  );

  const reactFlowInstance = useReactFlow();
  const onClick = useCallback(() => {
    const id = `${++nodeId}`;
    const newNode = {
      id,
      position: {
        x: Math.random() * 500,
        y: Math.random() * 500,
      },
      type: 'datasetnode'
    };
    reactFlowInstance.addNodes(newNode);
  }, []);

  var keys = {37: 1, 38: 1, 39: 1, 40: 1};

  function preventDefault(e) {
    e.preventDefault();
  }

  function preventDefaultForScrollKeys(e) {
    if (keys[e.keyCode]) {
      preventDefault(e);
      return false;
    }
  }

  function disableScroll() {
    window.addEventListener('DOMMouseScroll', preventDefault, false); // older FF
    window.addEventListener(wheelEvent, preventDefault, wheelOpt); // modern desktop
    window.addEventListener('touchmove', preventDefault, wheelOpt); // mobile
    window.addEventListener('keydown', preventDefaultForScrollKeys, false);
  }

  return (
    <>
      <link rel="stylesheet" href="https://fonts.googleapis.com/css?family=JetBrains+Mono" />
      <div style={{ backgroundColor: '#1C1C1C', height: 810, width: '15%', marginLeft: 0, marginRight: 'auto', float: 'left' }} onScroll={() => disableScroll()}>
        <button onClick={onClick} style={{ position: 'absolute' }}>
          Add Dataset Node
        </button>
      </div>
      <div style={{ height: 810, width: '85%', marginLeft: 'auto', marginRight: 0, float: 'right' }}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          nodeTypes={nodeTypes}
          fitView
          style={{ backgroundColor: '#1C1C1C' }}
        >
          <Background />
        </ReactFlow>
      </div>
    </>
  );
}

export default function () {
  return (
    <ReactFlowProvider>
      <Flow />
    </ReactFlowProvider>
  );
}
