import { useCallback } from 'react';
import { Handle, Position } from 'reactflow';
import { ChakraProvider, Editable, EditablePreview, EditableInput } from '@chakra-ui/react';

function ModifierNode() {
  const onChange = useCallback((evt) => {
    console.log(evt.target.value);
  }, []);

  const tableList = ['C01A', 'C01B', 'C02A']

  return (
    <ChakraProvider>
    <div className="modifier-node">
      <div>
        <label htmlFor="text" className="modifier-node_label">MODIFY</label>
        {
            tableList.map(x =>
                <>
                    <Editable defaultValue={x} className="modifier-node_editable">
                        <EditablePreview />
                        <EditableInput />
                    </Editable>
                    <br />
                </>
            )
        }
      </div>
      <Handle type="target" position={Position.Left} id="b"/>
      <Handle type="source" position={Position.Right} id="b"/>
    </div>
    </ChakraProvider>
  );
}

export default ModifierNode;
